	<script>
		function setCookie(cname, cvalue, exdays) {
			var d = new Date();
			d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
			var expires = "expires=" + d.toGMTString();
			document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
		}
		function getCookie(cname) {
			var name = cname + "=";
			var decodedCookie = decodeURIComponent(document.cookie);
			var ca = decodedCookie.split(';');
			for (var i = 0; i < ca.length; i++) {
				var c = ca[i];
				while (c.charAt(0) == ' ') {
					c = c.substring(1);
				}
				if (c.indexOf(name) == 0) {
					return c.substring(name.length, c.length);
				}
			}
			return "";
		}
		function addCookie($bookId) {
			var array_buku = [];
			array_checked = getCookie("checked");
			if (array_checked != "") {
				array_buku = JSON.parse(array_checked);
			}
			array_buku.push($bookId);
			setCookie("checked", JSON.stringify(Array.from(new Set(array_buku))), "3");
			//console.log(document.cookie);
		}
		function checkCookie($bookId) {
			var array_buku = [];
			array_checked = getCookie("checked");
			if (array_checked != "") {
				array_buku = JSON.parse(array_checked);
			}
			for (i = 0; i < array_buku.length; i++) {
				if ($bookId == array_buku[i]) {
					return true;
				}
			}
			return false;
		}
		function mendapatkan_provinsi() {
			jQuery.ajax({
				type: "POST",
				url: "babacms/function.php",
				data: "mode=provinsi",
				success: function(data) {
					jQuery("[name='dpId']").empty();
					jQuery("[name='dpId']").html(data);
				},
				dataType: "html"
			});
		}
		function mendapatkan_kota() {
			jQuery.ajax({
				type: "POST",
				url: "babacms/function.php",
				data: "mode=kota&province_id=" + jQuery("[name='dpId']").val(),
				success: function(data) {
					jQuery("[name='dcrId']").empty();
					jQuery("[name='dcrId']").html(data);
				},
				dataType: "html"
			});
		}
		function mendapatkan_distrik() {
			jQuery.ajax({
				type: "POST",
				url: "babacms/function.php",
				data: "mode=distrik&province_id=" + jQuery("[name='dpId']").val() + "&city_name=" + jQuery("[name='dcrId']").val(),
				success: function(data) {
					jQuery("[name='dist_id']").empty();
					jQuery("[name='dist_id']").html(data);
				},
				dataType: "html"
			});
		}
		function mendapatkan_sub_distrik() {
			jQuery.ajax({
				type: "POST",
				url: "babacms/function.php",
				data: "mode=sub_distrik&province_id=" + jQuery("[name='dpId']").val() + "&city_name=" + jQuery("[name='dcrId']").val() + "&dist_id=" + jQuery("[name='dist_id']").val(),
				success: function(data) {
					jQuery("[name='sub_dist_id']").empty();
					jQuery("[name='sub_dist_id']").html(data);
				},
				dataType: "html"
			});
		}
		function mendapatkan_kode_pos() {
			text_postcode = $("[name='sub_dist_id'").find('option:selected').text();
			array_text_postcode = text_postcode.split("-");
			postcode = array_text_postcode[0];
			$("[name='postcode']").val(postcode);
			if (typeof mendapatkan_daftar_jasa_pengiriman == 'function') {
				mendapatkan_daftar_jasa_pengiriman();
			}
		}
		function membersihkan_tampilan(city_name, dist_id, sub_dist_id, postcode) {
			if (city_name == 1) {
				jQuery("[name='dcrId']").empty();
			}
			if (dist_id == 1) {
				jQuery("[name='dist_id']").empty();
			}
			if (sub_dist_id == 1) {
				jQuery("[name='sub_dist_id']").empty();
			}
			if (postcode == 1) {
				jQuery("[name='postcode']").val('');
			}
		}
		jQuery(document).ready(function() {
			//mendapatkan_provinsi();
			jQuery("[name='dpId']").change(function() {
				membersihkan_tampilan(1, 1, 1, 1);
				mendapatkan_kota();
			});
			jQuery("[name='dcrId']").change(function() {
				membersihkan_tampilan(0, 1, 1, 1);
				mendapatkan_distrik();
